targets.query:
from(bucket: v.defaultBucket)
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "veeam_azure_overview")
  |> filter(fn: (r) => r["_field"] == "VMs" or r["_field"] == "VMsProtected")
  |> pivot(rowKey: ["_time"], columnKey: ["_field"], valueColumn: "_value")
  |> map(fn: (r) => ({
    _value: float(v: r.VMs) - float(v: r.VMsProtected)
  }))
  |> last()

